function showLoader() {
	$('.ac-loading').fadeTo(0, 0).css('display', 'block').fadeTo(200, 1);
	$('.ac-calendar tbody ').animate({
		'opacity': '0.7'
	}, 200);
}
function hideLoader() {
	$('.ac-loading').css('display', 'none');
}
$(document).ready(function() {
    $('.ac-dropdown-year').customSelect();    
	$(document).on('change', '.ac-dropdown-year', function(e) {
		showLoader();
		e.preventDefault();
		$.ajax({
			type: "POST",
			url: base_url + "calendar/functions/displayYearlyCalendar.php",
			data: {
				totalCalendars: $(".ac-total-calendars").html(),
				currentTimestamp: $(this).val(),
				calendarData: $(".ac-calendar-data").html(),
				calendarLegend: $(".ac-calendar-legend").html(),
				calendarHistory: $(".ac-calendar-history").html(),
				showDropdown: $(".ac-show-dropdown").html(),
				calendarLanguage: $(".ac-calendar-language").html(),
				weekStart: $(".ac-calendar-week-start").html(),
				calendarDirection: 'jump'
			}
		}).done(function(response) {
			$(".ac-calendar-months").html(response);
            $('.ac-dropdown-year').customSelect();            
			hideLoader();
		});
	});	
	$(document).on('mouseenter', '.ac-yearly-day:not(.disabled)', function() {
		var dm = $(this).attr('id').split("-");
		var day = dm[1];
		var month = dm[2];
		$("#ac-day-heading-" + day + " , #ac-month-heading-" + month).addClass('hover');
	}).on('mouseleave', '.ac-yearly-day:not(.disabled)', function() {
		$('.ac-month-heading.hover, .ac-day-heading.hover').removeClass('hover');
	})
})